    def rgb_to_ard(self, r, g, b):
        uvd = self.rgb_to_uvd(r, g, b)
        ard = self.uvd_to_ard(uvd[0], uvd[1], uvd[2])
        a = ard[0]
        r = ard[1]
        d = ard[2]
        if r == 0:
            a = self.angle_live
        return [a, r, d]
    def ard_to_rgb(self, a, r, d):
        uvd = self.ard_to_uvd(a, r, d)
        rgb = self.uvd_to_rgb(uvd[0], uvd[1], uvd[2])
        r = rgb[0]
        g = rgb[1]
        b = rgb[2]
        return [r, g, b]
    # HUE
    def rgb_to_hue(self, r, g, b):
        maxc = max(r, g, b)
        minc = min(r, g, b)
        if minc == maxc:
            # return [0.0]
            return [self.angle_live]
        rc = (maxc-r) / (maxc-minc)
        gc = (maxc-g) / (maxc-minc)
        bc = (maxc-b) / (maxc-minc)
        if r == maxc:
            h = bc-gc
        elif g == maxc:
            h = 2.0 + rc - bc
        else:
            h = 4.0 + gc - rc
        h = ( h / 6.0 ) % 1.0
        return [h]
    def hue_to_rgb(self, h):
        vh = h * 6
        if vh == 6 :
            vh = 0
        vi = int( vh )
        v2 = 1 * ( 1 - 1 * ( vh - vi ) )
        v3 = 1 * ( 1 - 1 * ( 1 - ( vh - vi ) ) )
        if vi == 0 :
            r = 1
            g = v3
            b = 0
        elif vi == 1 :
            r = v2
            g = 1
            b = 0
        elif vi == 2 :
            r = 0
            g = 1
            b = v3
        elif vi == 3 :
            r = 0
            g = v2
            b = 1
        elif vi == 4 :
            r = v3
            g = 0
            b = 1
        else:
            r = 1
            g = 0
            b = v2
        return [r, g, b]
