    def hsi_to_rgb_old(self, h, s, i):
        # HSX to sRGB
        hue = h * 6
        z = (1 - abs(math.fmod(hue, 2) - 1) ) * s
        c = (3 * i * s) / (1 + z)
        x = c * z
        if (hue >= 0 and hue < 1):
            r = c
            g = x
            b = 0
        elif (hue >= 1 and hue < 2):
            r = x
            g = c
            b = 0
        elif (hue >= 2 and hue < 3):
            r = 0
            g = c
            b = x
        elif (hue >= 3 and hue < 4):
            r = 0
            g = x
            b = c
        elif (hue >= 4 and hue < 5):
            r = x
            g = 0
            b = c
        elif (hue >= 5 and hue <= 6):
            r = c
            g = 0
            b = x
        else:
            r = 0
            g = 0
            b = 0
        m = i * ( 1 - s )
        r = r + m
        g = g + m
        b = b + m
        # In case Krita is in Linear Format
