    def lab_to_lch(self, l, a, b):
        vh = math.atan( b, a )
        if vh > 0:
            vh = ( vh / math.pi ) * 180
        else:
            vh = 360 - ( abs( vh ) / math.pi ) * 180
        l = l
        c = math.sqrt( a ** 2 + b ** 2 )
        h = vh
        return [l, c, h]
    def lch_to_lab(self, l, c, h):
        l = l
        a = math.cos( radians(h) ) * c
        b = math.sin( radians(h) ) * c
        return [l, a, b]
