    def Color_INTERPOLATE(self, space, factor, cor1, cor2):
        # Calculations
        if space == "AAA":
            # Deltas
            d0 = cor2[0] - cor1[0]
            # Linear Interpolation
            a = cor1[0] + (d0 * factor)
        elif space == "CMYK":
            # Deltas
            d0 = cor2[0] - cor1[0]
            d1 = cor2[1] - cor1[1]
            d2 = cor2[2] - cor1[2]
            d3 = cor2[3] - cor1[3]
            # Linear Interpolation
            a = cor1[0] + (d0 * factor)
            b = cor1[1] + (d1 * factor)
            c = cor1[2] + (d2 * factor)
            d = cor1[3] + (d3 * factor)
        else:
            # Deltas
            d0 = cor2[0] - cor1[0]
            d1 = cor2[1] - cor1[1]
            d2 = cor2[2] - cor1[2]
            # Linear Interpolation
            a = cor1[0] + (d0 * factor)
            b = cor1[1] + (d1 * factor)
            c = cor1[2] + (d2 * factor)
        # Convert into RGB
        if space == "AAA":
            aaa = [a]
            rgb = [a, a, a]
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "RGB":
            rgb = [a, b, c]
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "CMY":
            cmy = [a, b, c]
            rgb = self.cmy_to_rgb(cmy[0], cmy[1], cmy[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "CMYK":
            cmyk = [a, b, c, d]
            rgb = self.cmyk_to_rgb(cmyk[0], cmyk[1], cmyk[2], cmyk[3])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "RYB":
            ryb = [a, b, c]
            rgb = self.ryb_to_rgb(ryb[0], ryb[1], ryb[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "YUV":
            yuv = [a, b, c]
            rgb = self.yuv_to_rgb(yuv[0], yuv[1], yuv[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "ARD":
            ard = [a, b, c]
            rgb = self.ard_to_rgb(ard[0], ard[1], ard[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "HSV":
            hsv = [a, b, c]
            rgb = self.hsv_to_rgb(hsv[0], hsv[1], hsv[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "HSL":
            hsl = [a, b, c]
            rgb = self.hsl_to_rgb(hsl[0], hsl[1], hsl[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "HCY":
            hcy = [a, b, c]
            rgb = self.hcy_to_rgb(hcy[0], hcy[1], hcy[2])
            xyz = self.rgb_to_xyz(rgb[0], rgb[1], rgb[2])
        if space == "XYZ":
            xyz = [a, b, c]
            rgb = self.xyz_to_rgb(xyz[0], xyz[1], xyz[2])
        if space == "XYY":
            xyy = [a, b, c]
            xyz = self.xyy_to_xyz(xyy[0], xyy[1], xyy[2])
            rgb = self.xyz_to_rgb(xyz[0], xyz[1], xyz[2])
        if space == "LAB":
            lab = [a, b, c]
            xyz = self.lab_to_xyz(lab[0], lab[1], lab[2])
            rgb = self.xyz_to_rgb(xyz[0], xyz[1], xyz[2])
        # Return
        return [ [rgb[0], rgb[1], rgb[2]], [xyz[0], xyz[1], xyz[2]] ]
    # Color Floor
    def Color_Correction(self):
        if self.d_cm == "RGBA":
            # Floor RGB
            self.rgb_f1 = (int(math.floor(self.rgb_1 * k_RGB))) / k_RGB
            self.rgb_f2 = (int(math.floor(self.rgb_2 * k_RGB))) / k_RGB
            self.rgb_f3 = (int(math.floor(self.rgb_3 * k_RGB))) / k_RGB
            # Floor RGB to Others
            aaa = self.rgb_to_aaa(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.aaa_f1 = aaa[0]
            cmy = self.rgb_to_cmy(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.cmy_f1 = cmy[0]
            self.cmy_f2 = cmy[1]
            self.cmy_f3 = cmy[2]
            cmyk = self.rgb_to_cmyk(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.cmyk_f1 = cmyk[0]
            self.cmyk_f2 = cmyk[1]
            self.cmyk_f3 = cmyk[2]
            self.cmyk_f4 = cmyk[3]
            ryb = self.rgb_to_ryb(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.ryb_f1 = ryb[0]
            self.ryb_f2 = ryb[1]
            self.ryb_f3 = ryb[2]
            yuv = self.rgb_to_yuv(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.yuv_f1 = yuv[0]
            self.yuv_f2 = yuv[1]
            self.yuv_f3 = yuv[2]
            ard = self.rgb_to_ard(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.ard_f1 = ard[0]
            self.ard_f2 = ard[1]
            self.ard_f3 = ard[2]
            hsv = self.rgb_to_hsv(self.rgb_f1, self.rgb_f2, self.rgb_f3)
            self.hsv_f1 = hsv[0]
