    # IMG Panel
    def Update_Panel_IMG(self, update):
        if (update != "UPDATE" or self.img_path != self.img_old):
            # Read Image
            image = QImage(str(self.img_path))
            if self.img_grayscale == True:
                image = image.convertToFormat(24) # QImage::Format_Grayscale16
            self.source = QPixmap.fromImage(image)
            # Dimensions to consider
            src_width = self.source.width()
            src_height = self.source.height()
            img_width = self.layout.panel_img_mix.width()
            img_height = self.layout.panel_img_mix.height()
            # Calibrate Size with Current Image
            if (src_width > img_width or src_height > img_height):
                self.source_scale = self.source.scaled(img_width, img_height, Qt.KeepAspectRatio, Qt.FastTransformation)
                self.img_box_w = self.source_scale.width()
                self.img_box_h = self.source_scale.height()
            else:
                self.source_scale = self.source.scaled(src_width, src_height, Qt.IgnoreAspectRatio, Qt.FastTransformation)
                self.img_box_w = src_width
                self.img_box_h = src_height
            # Delta Values for Frame
            self.img_box_w2 = self.img_box_w * 0.5
            self.img_box_h2 = self.img_box_h * 0.5
            self.img_box_l = (img_width - self.img_box_w) * 0.5
            self.img_box_t = (img_height - self.img_box_h) * 0.5
            self.img_box_cx = (img_width * 0.5)
            self.img_box_cy = (img_height * 0.5)
            # Show Pixmap
            self.layout.image.setPixmap(self.source_scale)
            # Widget Geometry
            self.layout.image.setGeometry(0, 0, img_width, img_height)
            self.layout.image_cursor.setGeometry(0, 0, img_width, img_height)
            # Cursor Location
            self.panel_img.Location(
                self.img_box_l + (self.img_location_x * self.img_box_w),
                self.img_box_t + (self.img_location_y * self.img_box_h),
                self.img_box_w,
                self.img_box_h
                )
            # Label Path display
            if self.names_display == True:
                self.layout.label.setText(str(self.img_name))
            # Control Variable
            self.img_old = self.img_path
