    def Signal_IMG_Color(self, SIGNAL_IMG_COLOR):
        # Geometry
        img_width = self.layout.image.width()
        img_height = self.layout.image.height()
        pixmap = self.layout.image.grab(QRect(QPoint(0, 0), QPoint(img_width, img_height)))
        image = pixmap.toImage()
        # Limit Range to Pick Pixel
        vx = SIGNAL_IMG_COLOR[0]
        vy = SIGNAL_IMG_COLOR[1]
        if vx <= (self.img_box_cx - self.img_box_w2):
            vx = self.img_box_cx - self.img_box_w2
        if vx >= (self.img_box_cx + self.img_box_w2):
            vx = self.img_box_cx + self.img_box_w2 -1
        if vy <= (self.img_box_cy - self.img_box_h2):
            vy = self.img_box_cy - self.img_box_h2
        if vy >= (self.img_box_cy + self.img_box_h2):
            vy = self.img_box_cy + self.img_box_h2 -1
        # Color At Pixel
        color = image.pixelColor(vx, vy)
        # Apply Color Values
        if (color.red() != 0 and color.green() != 0 and color.blue() != 0):
            self.rgb_1 = color.red()/255
            self.rgb_2 = color.green()/255
            self.rgb_3 = color.blue()/255
            # Apply Colors
            self.Color_ANGLE(self.rgb_1, self.rgb_2, self.rgb_3)
            self.Color_APPLY("RGB", self.rgb_1, self.rgb_2, self.rgb_3, 0)
            self.layout.label_percent.setText("")
        # Percentual Cursor (0-1)
        if self.img_box_w == 0:
            self.img_location_x = 0
        else:
            self.img_location_x = (vx - self.img_box_l) / self.img_box_w
        if self.img_box_h == 0:
            self.img_location_y = 0
        else:
            self.img_location_y = (vy- self.img_box_t) / self.img_box_h
